import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    title: '',
    image: '',
    link: '',
    currentChapter: ''
}

const newMangaSlice = createSlice({
    name: 'newMange',
    initialState,
    reducers: {
        handleChange: (state, event) => {
            console.log("event", event)
            state = {...state, [event.target.name]: event.target.value}
        }
    }
})

export const {handleChange} = newMangaSlice.actions
export default newMangaSlice.reducer
