import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    title: "",
    image: "",
    totalChapters: 0,
    currentChapter: 0,
}

const mangaSlice = createSlice({
    name: "manga",
    initialState,
    reducers: {
        changeTitle: (state) => {
            state.title = "Test"
        },
        changeTotalChapters: (state) => {
            state.totalChapters = 10;
        },
        changeCurrentChapter: (state) => {
