import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

const initialState = [{
    title: "",
    image: "",
    totalChapters: 0,
    currentChapter: 0,
}]

// export const getMangaList = createAsyncThunk('manga/getMangaList', () =>{
//     fetch("http://localhost:8000/manga/")
//     .then(res => console.log(res.data))
// })

const mangaSlice = createSlice({
    name: "manga",
    initialState,
    reducers: {
        changeTitle: (state) => {
            state.title = "Test"
        },
        changeTotalChapters: (state) => {
            state.totalChapters = 10;
            console.log(state)
        },
        changeCurrentChapter: (state) => {
            state.currentChapter = 1;
        },
        getMangaList: (state) => {
            var mangaList
            fetch("http://localhost:8000/manga/")
            .then(res => res.json())
            .then(data => mangaList = data)
            setTimeout(() => {
                console.log(mangaList)
                state = mangaList
                console.log("State: ", state)
            }, 1000);
        }
    }
})

// console.log(viewSlice)

export const {changeTitle, changeTotalChapters, changeCurrentChapter} = mangaSlice.actions;
export default mangaSlice.reducer;
