import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    title: "",
    totalChapters: 0,
    currentChapter: 0,
}

const viewSlice = createSlice({
    name: "manga",
    initialState,
    reducers: {
        changeTitle: (state) => {
            state.view = "mangaList"
        }
    }
})

// console.log(viewSlice)

export const {changeView} = viewSlice.actions;
export default viewSlice.reducer;
